import os
import requests
from typing import Optional
from mcp.server.fastmcp import FastMCP

mcp = FastMCP(name="Dify Workflow", port=int(os.getenv("PORT", 8000)))

@mcp.tool()
def run_workflow(input: str="") -> Optional[str]:
    """
    <详细描述此工具的作用，大模型会看到>
    """
    # 从环境变量获取配置
    api_key = os.getenv('DIFY_API_KEY', '')
    user_id = os.getenv('DIFY_USER_ID', '')
    response_mode = os.getenv('DIFY_RESPONSE_MODE', 'blocking')  # 默认值为'blocking'

    # 验证必要配置是否存在
    if not api_key:
        raise ValueError("缺少必要环境变量: DIFY_API_KEY")
    if not user_id:
        raise ValueError("缺少必要环境变量: DIFY_USER_ID")
    if not response_mode:
        raise ValueError("缺少必要环境变量: DIFY_RESPONSE_MODE")

    url = "https://api.dify.ai/v1/workflows/run"
    headers = {
        "Authorization": f"Bearer {api_key}",
        "Content-Type": "application/json"
    }
    payload = {
        "inputs": {
            "input": input
        },
        "response_mode": response_mode,
        "user": user_id
    }

    try:
        print("start request Dify.ai API...")
        response = requests.post(url, json=payload, headers=headers)
        response.raise_for_status()  # 如果请求失败会抛出HTTPError
        result = response.json()

        if result is not None:
            print("success！")
            return result
        else:
            print("返回结果为空")
            return "返回结果为空"
    except requests.exceptions.RequestException as e:
        print(f"API请求失败: {e}")
        return None
    except Exception as e:
        print(f"发生意外错误: {str(e)}")
        return None


if __name__ == "__main__":
    mcp.run(transport="sse")

